#include "GPIO_D.h"
/*
** ===================================================================
**     Method      :  GPIO_D_Init (component Init_GPIO)
**
**     Description :
**         This method initializes registers of the GPIO module
**         according to this Peripheral Initialization settings. Call
**         this method in the user code to initialize the module. By
**         default, the method is called by PE automatically; see "Call
**         Init method" property of the component for more details.
**     Parameters  : None
**     Returns     : Nothing
** ===================================================================
*/
void GPIO_D_Init(void)
{
  /* PORTD: PD6=0,PD5=0,PD4=0,PD3=0,PD2=0,PD1=0,PD0=0 */
  clrReg8Bits(PORTD, 0x7FU);            
  /* PUCR: PUPDE=0 */
  clrReg8Bits(PUCR, 0x08U);             
  /* DDRD: DDRD6=1,DDRD5=1,DDRD4=1,DDRD3=1,DDRD2=1,DDRD1=1,DDRD0=1 */
  setReg8Bits(DDRD, 0x7FU);             
}

